            msgsProtocolo.push('Encaminhamos o retorno do seu protocolo no seu e-mail cadastrado no banco Safra. Pedimos que verifique a sua caixa de entrada. Caso não encontre, não se esqueça de olhar na sua caixa de spam.');
        }
        msgsProtocolo = msgsProtocolo.join("\n\n");
        return msgsProtocolo;
    }
    catch (e) {
        return e.message;
    }
}
function getDataFormatada(data) {
    let dataHoraInclusao = new Date(data);
    let dia = ("0" + dataHoraInclusao.getDate()).slice(-2);
    let mes = ("0" + (dataHoraInclusao.getMonth() + 1)).slice(-2);
    let ano = dataHoraInclusao.getFullYear();
    return `${dia}/${mes}/${ano}`;
};

function getAddDoisDiasUteis(data,maiorSLAOcorrencia) {
    data = new Date(data);
    maiorSLAOcorrencia = maiorSLAOcorrencia ? parseInt(maiorSLAOcorrencia) : 4;
    for (let i = 0; i < maiorSLAOcorrencia;) {
        data.setDate(data.getDate() + 1);
        if (data.getDay() !== 0 && data.getDay() !== 6) {
            i++;
        }
    }
    return getDataFormatada(data);
}

function ignoreBlock(stateName) {
    try {
        const match = stateName.match(/\[(.*?)\]/);
        const stateNameOrigin = match ? match[1] : stateName;
        const regex = /Redirect|Redirecionar/i;
        const ignore = !regex.test(stateNameOrigin);
        return ignore;
    } catch (err) {
        return false
    }
}


function run(voltarState, blockId, botOrigin, lastVoltarState, previousStateName) {
    const state = JSON.parse(voltarState)
    try {
        if (!voltarState) {
            const state = {}
            state[botOrigin] = [blockId]
            return state
        }
        const isIgnoredBlock = ignoreBlock(previousStateName)
        if (state[botOrigin][state[botOrigin].length - 1] !== blockId && blockId !== 'onboarding' && !isIgnoredBlock && blockId !== lastVoltarState) {
            state[botOrigin].push(blockId)
        }
        return state
    } catch (err) {
        state[botOrigin] = [blockId]
        return state
    }
}
