function run(dataAvaliacao) {

    if(!dataAvaliacao){
        return 'true';
    }

    let dateWithoutHours;
    let newDateWithouHoures;
    if(/^\d{4}\-\d{1,2}\-\d{1,2}/.test(dataAvaliacao))
    {
        dateWithoutHours =  dataAvaliacao.split('T')[0].split("-");
        newDateWithouHoures = dateWithoutHours[1] + '/' + dateWithoutHours[2] + '/' + dateWithoutHours[0];
    }
    else {
        dateWithoutHours =  dataAvaliacao.split(' ')[0].split("/");
        newDateWithouHoures = dateWithoutHours[1] + '/' + dateWithoutHours[0] + '/' + dateWithoutHours[2];
    }

    let date = new Date();
    let day = date.getUTCDate() < 10 ? "0" + date.getUTCDate() : date.getUTCDate();
    let month = (date.getUTCMonth() + 1) < 10 ? "0" + (date.getUTCMonth() + 1) : (date.getUTCMonth() + 1);
    let dateNow = month + "/" + day + "/" + date.getUTCFullYear();
    let date1 = new Date(newDateWithouHoures); 
    let date2 = new Date(dateNow);
    let  Difference_In_Time = date2.getTime() - date1.getTime(); 
    let Difference_In_Days = Difference_In_Time / (1000 * 3600 * 24); 

    const retorno  =  Difference_In_Days > 30 ? true : false;

    return retorno;
   }

