 * @property {string} cpfAppKey
 * @property {(boolean|string)} ec
 * @property {string} segmentoGerente
 */

/**
 * @param {ContaSelecionada} contaSelecionada - Objeto contendo as informações da conta selecionada
 * @description Dentro do try, defina todas as regras de exibição, utilizando as funções disponiveis: "verificaPermissao", "atualizaOpcaoMenu", "adicionaOpcaoNoMenu"
 */
function run(contaSelecionada) {
    try {
        const { segmentoGerente, permissoes } = JSON.parse(contaSelecionada)

        if (!segmentoGerente) {
            removeOpcaoDoMenu({ tituloDoBotao: 'Outras credenciadoras' })
        }

        verificaPermissao({ tituloDoBotao: 'Cancelamento', permissoesNecessarias: ['cancelarAntecipAutomatica'], permissoes })
        return {
            "recipient_type": "individual",
            "type": "interactive",
            "interactive": {
                "type": "list",
                "header": {
                    "type": "text",
                    "text": ""
                },
                "body": {
                    "text": `Ótimo, o que você precisa sobre antecipação?`
                },
                "action": {
                    "button": "Ver opções",
                    "sections": [
                        {
                            "title": "",
                            "rows": menuOptions
                        }
                    ]
                }
            }
        }
    } catch (err) {
