function run(inputUsuario, salesPaymentType) {
  const invalidInputMessage = 'input inesperado'
  try {
    if (!inputUsuario) {
      return 'inatividade'
    }
    let regexDictionary = {
      "venda f[ií]sica": "Venda Física",
      "venda online|venda digital": "Venda Online"
    }
    for (let regex in regexDictionary) {
      if (new RegExp(regex, 'i').test(inputUsuario)) {
        return regexDictionary[regex]
      }
    }
    return invalidInputMessage
  } catch (err) {
    return invalidInputMessage
  }
}

