		let codigoTipoEquipamento = consultMaquina[0].codigoTipoEquipamento
		codigoTipoEquipamento = codigoTipoEquipamento.toString()
		if (consultMaquina == "C") {
			if (codigoTipoEquipamento == 1 || codigoTipoEquipamento == 5) {
				codigoTipoEquipamento = 'SafraPay Mini'
			} else if (codigoTipoEquipamento == 2) {
				codigoTipoEquipamento = 'SafraPay Slim'
			} else if (codigoTipoEquipamento == 3) {
				codigoTipoEquipamento = 'SafraPay Pro'
			} else {
				codigoTipoEquipamento = 'SafraPay Smart'
			}
		} else {
			if (codigoTipoEquipamento == 15) {
				codigoTipoEquipamento = 'SafraPay Mini'
			} else if (codigoTipoEquipamento == 5) {
				codigoTipoEquipamento = 'SafraPay Slim'
			} else if (codigoTipoEquipamento == 1) {
				codigoTipoEquipamento = 'SafraPay Pro'
			} else {
				codigoTipoEquipamento = 'SafraPay Smart'
			}
		}
		return codigoTipoEquipamento;
	}
	catch (e) {
		return e.message;
	}
}

