function run(bodyDomicilio, bodyGeralDomicilio, bandeira) {
    try {
        const bodyDomicilioObj = JSON.parse(bodyDomicilio);
        const bodyGeralDomicilioObj = JSON.parse(bodyGeralDomicilio);
        let codigoBandeira = bandeira.toUpperCase();
        codigoBandeira = codigoBandeira.replace('MASTER', 'MCRD').replace('VISA', 'VISA').replace('HIPER', 'HIPR').replace('ELO', 'ELO').replace('AMEX', 'AMEX');

        const getDomicilioDataPorBandeira = (codigoBandeira, propriedade) => {
            const filteredData = bodyDomicilioObj.data.filter(item =>
                item.codigoBandeira.trim() == codigoBandeira.trim() &&
                (item.codigoProduto == "01" || item.codigoProduto == "02")
            );

            return filteredData.map(item => {
                if (propriedade == 'banco') {
                    return item.domicilio[propriedade].replace(/^0+/, '').padStart(3, '0') || null;
                }
                if (propriedade == 'codigoProduto') {
                    return item[propriedade] || 'null';
                }
                return item.domicilio[propriedade] || 'null';
            });
        }
        const getNomeBancoPorCodigo = (codigoBanco) => {
            return codigoBanco.map(codigo => {
                const banco = bodyGeralDomicilioObj.data.find(item => item.codigo === codigo);
                return banco ? banco.nome : null;
            });
        }

        let domicilioDados = {};
        const codigoProduto = getDomicilioDataPorBandeira(codigoBandeira, 'codigoProduto');
        const codigosBanco = getDomicilioDataPorBandeira(codigoBandeira, 'banco');
        const codigosAgencia = getDomicilioDataPorBandeira(codigoBandeira, 'agencia');
        const codigosContaCorrente = getDomicilioDataPorBandeira(codigoBandeira, 'contaCorrente');
        const codigosDigito = getDomicilioDataPorBandeira(codigoBandeira, 'digitoContaCorrente');
        const nomesBanco = getNomeBancoPorCodigo(codigosBanco);

        for (let i = 0; i < codigosBanco.length; i++) {
            domicilioDados[`codigoBanco${i + 1}`] = codigosBanco[i];
        }
        for (let i = 0; i < codigosAgencia.length; i++) {
            domicilioDados[`codigoAgencia${i + 1}`] = codigosAgencia[i].replace(/^0+/, '');
        }
        for (let i = 0; i < codigosDigito.length; i++) {
            domicilioDados[`codigoDigito${i + 1}`] = codigosDigito[i];
        }
        for (let i = 0; i < nomesBanco.length; i++) {
            domicilioDados[`nomeBanco${i + 1}`] = nomesBanco[i];
        }
        for (let i = 0; i < codigosContaCorrente.length; i++) {
            domicilioDados[`contaCorrente${i + 1}`] = codigosContaCorrente[i].replace(/^0+/, '');
        }
        if (codigoProduto.includes("01") && codigoProduto.includes("02")) {
            domicilioDados.codigoProduto = "Ambos";
        }
        else if (codigoProduto.includes("02")) {
            domicilioDados.codigoProduto = "Crédito";
        } else if (codigoProduto.includes("01")) {
            domicilioDados.codigoProduto = "Débito";
        }
        return domicilioDados;

    } catch (err) {
        return err.message;
    }
}

