function run(consultMaquina) {
  try {
    let consultMaquina = JSON.parse(consultMaquina);

    function retronarModalidadeA(value) {
      if (value.modalidade == 'A')
        return value;
    }
    function retronarModalidadeC(value) {
      if (value.modalidade == 'C')
        return value;
    }
    function retronarModalidadeV(value) {
      if (value.modalidade == 'V')
        return value;
    }

    let var1 = consultMaquina.data.filter(retronarModalidadeA);
    let var2 = consultMaquina.data.filter(retronarModalidadeC);
    let var3 = consultMaquina.data.filter(retronarModalidadeV);
    let retorno = ""
    if (var1.length > 0 && var3.length > 0 || var2.length > 0 && var3.length > 0) {
      retorno = "A e C"
    } else if (var1.length > 0 || var2.length > 0) {
      retorno = "A"
    } else {
      retorno = "C"
    }
    return retorno;
  } catch (e) {
    return e.message;
  }

}

