function run(input, inputType, customerAccounts) {
  const error = JSON.stringify({
    status: "input inválido",
    input: input ? input : "usuário inativo",
    isValid: false,
    type: "menuDinamico",
  });

  if (inputType !== "text/plain") return error;

  customerAccounts = JSON.parse(customerAccounts);
  input = input.trim();

  // A opção e o número da opção são incluidos automaticamente na regexp
  const menuOptions = {
    "Encerrar atendimento": ["encerrar", "sair"],
  };
  const options = ["Encerrar atendimento"];
  const accounts = customerAccounts.accounts;
  const charLimit = 24;

  accounts.forEach((account) => {
    const textOption = `Conta: ${account.numeroConta}`;

    if (textOption.length < charLimit) {
      menuOptions[account.numeroConta] = [textOption];
    } else {
      menuOptions[account.numeroConta] = [];
    }

    options.push(account.numeroConta);
  });

  const matches = options.find((option, index) => {
    let regexp = `^${option}$|`;
    menuOptions[option].forEach((validationString) => {
      validationString = validationString.replace(/^\^|\$$/g, "");
      regexp += `^${validationString}$|`;
    });
    regexp += `^${index}$`;

    return input.match(new RegExp(regexp, "gim"));
  });

  return matches
    ? JSON.stringify({
        status: "input válido",
        input: matches,
        isValid: true,
        type: "menuDinamico",
      })
    : error;
}

