function run(input) {
    try {
        if (input.includes('latitude') && input.includes('longitude')) {
            return 'location';
        } else if (input.includes('phoneNumber') || input.includes('cellPhoneNumber')) {
            return 'contact';
        } else if (input.includes('type')) {
            input = JSON.parse(input.replace(/\\/g, ''));
            return input.type;
        }
        return 'text/plain';
    } catch (err) {
        return 'text/plain'
    }
}

const run = (inputType) => {
    return inputType != 'text/plain' ? 'Sim' : 'Nao';
}

const run = (inputCVType) => {
    try {
        const match = matchKey(MEDIA_TYPES, inputCVType);
        return MEDIA_TYPES[match] || MEDIA_TYPES["default"];

    } catch (e) {
        return MEDIA_TYPES['default']
    }
}

const MEDIA_TYPES = {
    "audio": "EX.5.0.0",
    "image": "EX.5.0.1",
    "sticker": "EX.5.0.1",
    "video": "EX.5.0.2",
    "location": "EX.5.0.3",
    "file": "EX.5.0.4",
    "pdf": "EX.5.0.4",
    "emoji": "EX.5.0.5",
    "contact": "EX.5.0.6",
    default: "E.3.5.0"
};

//string extension "contains".
Object.defineProperty(String.prototype, "contains", {
    value: function contains(str) {
        return this.indexOf(str) > -1;
    },
    writable: true,
    configurable: true
});

const matchKey = (options, input) => {
    return Object.keys(options).find(key => (
        input.contains(key)
    ));
}

function ValidarConteudo(inputOriginType) {
    const starts = [
        "application/",
        "audio/",
        "video/",
        "image/",
        "link",
        "location",
        "contact",
        "application/zip"
    ];
    const result = starts.find(item => inputOriginType.startsWith(item));
    return result ? result : 'Não encontrado';
}
function run(inputContent, inputType) {
    try {
        let input = ValidarConteudo(inputType);
        if (input == 'Não encontrado') {
            if (inputContent.includes("type")) {
                return JSON.parse(inputContent.replace(/\\/g, '')).type;
            }
            else if (inputContent.includes("http")) {
                return "link/";
            } else if (inputContent.includes("latitude") && inputContent.includes("longitude")) {
                return "location";
            } else if (inputContent.includes("phoneNumber") || inputContent.includes("cellPhoneNumber")) {
                return "contact";
            }
        }
        return inputContent;
    }
    catch (e) {
        return e.message;
    }
}

function run(voltarState, botOrigin) {
	try {
		const { botOrigin } = JSON.parse(botOrigin)
		const state = JSON.parse(voltarState)
		return state[botOrigin][state[botOrigin].length - 1]
	} catch (err) {
		return 'onboarding'
	}
}

function run(voltarState, botOrigin) {
	try {
		const { botOrigin } = JSON.parse(botOrigin)
		const state = JSON.parse(voltarState)
		if (state[botOrigin].length === 0) {
			return state
		}
		state[botOrigin].pop()
		return state
	} catch (err) {
		return false
	}
}

function run(voltar, state, stateOrigin, lastInput, intent, env) {
	try {
		const parsedState = JSON.parse(state)
		const voltarState = JSON.parse(voltar)
		if (voltar == false) {
			throw new Error('reset')
		}

		if (parsedState.botOrigin !== `Bot Main ${env || ''}`.trim() && voltarState[parsedState.botOrigin].length === 0) {
			return {
				botDestination: `Bot Main ${env || ''}`.trim(),
				botOrigin: 'cascata',
				stateDestination: voltarState[`Bot Main ${env || ''}`.trim()][voltarState[`Bot Main ${env || ''}`.trim()].length - 1],
				stateOrigin,
				lastInput,
				intent
			}
		}
		const id = voltarState[parsedState.botOrigin][voltarState[parsedState.botOrigin].length - 1]
		return {
			botDestination: parsedState.botOrigin,
			botOrigin: 'cascata',
			stateDestination: id,
			stateOrigin,
			lastInput,
			intent
		}
	} catch (err) {
		return {
			botOrigin: 'cascata',
			stateOrigin,
			botDestination: `Bot Main ${env || ''}`.trim(),
			stateDestination: 'welcome',
			err: err.message
		}
	}
}

function checkVariables(contaSelecionada, intent) {
  try {
      let variables = []
      if (contaSelecionada) {
          contaSelecionada = JSON.parse(contaSelecionada)
          if (!intent.segmento.includes(contaSelecionada.tipoPessoa)) {
              return variables
          }
          const keyContaSelecionada = Object.keys(contaSelecionada)
          keyContaSelecionada.forEach((key) => {
              if (contaSelecionada[key] && contaSelecionada[key] !== 'false') {
                  return variables.push(key)
              }
          })
          contaSelecionada.permissoes.forEach((permissao) => {
              variables.push(permissao)
          })
      }
      return variables
  } catch (err) {
      return []
  }
}

function run(intencaoIdentificada, contaSelecionada, intentDestination) {
  try {
      const intent = JSON.parse(intentDestination).find(intent => intent.intent.toLowerCase() === intencaoIdentificada.toLowerCase())
      const userContext = {
          variables: checkVariables(contaSelecionada, intent),
          destinationBlock: '',
          allowed: false,
          auth: false
      }
      if (intent) {
          if (intent.requirements.length === 0 || intent.requirements[0] === '') {
              userContext.allowed = true;
              userContext.destinationBlock = intent.destinationBlock;
              userContext.destinationBot = intent.destinationBot
          } else {
              userContext.allowed = intent.requirements.every(requirement => userContext.variables.includes(requirement));
              if (userContext.allowed) {
                  userContext.destinationBlock = intent.destinationBlock;
                  userContext.destinationBot = intent.destinationBot
                  userContext.auth = intent.auth
              }
          }
      }
      return userContext
  } catch (err) {
      return {
