function run(calendarDatetime, inputMessage, inputContent, broadcast, identity) {
    const date = new Date(calendarDatetime);

    const dataFormatada = dataAtualFormatada(date);
    let metadataCampanha = metadata(inputMessage);
    const origem = origemCampanha(inputContent, metadataCampanha, broadcast);
    const canal = origemCanal(identity);
    metadataCampanha = metadataCampanha != "" ? metadataCampanha : "nao possui campanha";

    const trackOrigemCampanha = `${dataFormatada} | ${identity} | ${metadataCampanha} | ${canal} | ${origem} | ${inputContent}`

    return { 
        trackOrigemCampanha,
        canal
    };
}

function dataAtualFormatada(data) {
    const dia = data.getDate().toString();
    const diaF = (dia.length == 1) ? '0' + dia : dia;
    const mes = (data.getMonth() + 1).toString(); //+1 pois no getMonth Janeiro começa com zero.
    const mesF = (mes.length == 1) ? '0' + mes : mes;
    const anoF = data.getFullYear();
    const hora = data.getHours().toString();
    const horaF = (hora.length == 1) ? '0' + hora : hora;
    const min = data.getMinutes().toString();
    const minF = (min.length == 1) ? '0' + min : min;
    const seg = data.getSeconds().toString();
    const segF = (seg.length == 1) ? '0' + seg : seg;

    return `${diaF}/${mesF}/${anoF} ${horaF}:${minF}:${segF}`;
}

function metadata(inputMessage) {
    try {
        let inputMessageFormatted = JSON.parse(inputMessage);
        // let inputMessageFormatted = inputMessage;
        let metadata = inputMessageFormatted.metadata;

        if (metadata && metadata['#wa.referral.headline']) {
            const metadataCampanha = `${metadata['#wa.referral.source_id']} | ${metadata['#wa.referral.source_url']} | ${metadata['#wa.referral.headline']} | ${metadata['#wa.referral.body']} | ${metadata['#wa.referral.source_type']} | ${metadata['#wa.referral.image.id']}`
            return metadataCampanha;
        } else {
            const metadataCampanha = ""
            return metadataCampanha;
        }
    } catch (err) {
        const metadataCampanha = "";
        return metadataCampanha;
    }
}

function origemCampanha(inputContent, metadata, broadcast) {
    try {
        const sourceUrl = metadata;
        if (sourceUrl != "") {
            return "Campanha"
        } else if(broadcast){
            return `broadcast | ${broadcast}`
        }
        return "organico"
    } catch (err) {
        return "organico"
    }
}

function origemCanal(identity) {
    try {
        const source = identity.split('@')[1].split('.')[0];
        switch (source) {
            case 'wa':
                return "whatsapp";
                break;
            case 'businesschat':
                return "businesschat";
                break;
            case '0mn':
                return "blipchat";
                break;
            case 'messenger':
                return "messenger";
                break;
            case 'telegram':
                return "telegram";
                break;
            case 'instagram':
                return "instagram";
                break;
            case 'mailgun':
                return "email";
                break;
            case 'workplace':
                return "workchat";
                break;
            case 'tunnel':
                return "whatsapp";
                break;
            case 'abs':
                return "teams";
                break;
            default:
                return "new"
                break;
        }

    } catch (error) {
        return false;
    }
}

