function run(state, serviceName) {
	try {
		const { botDestination, stateDestination } = JSON.parse(state);
		if (stateDestination.match(/^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/) && botDestination === serviceName) {
			return stateDestination
		}
		return false
	} catch (err) {
		return false
	}
}

function run(stateName) {
    try {
        const regex = new RegExp("FC(.\\d{1,2}){2,}", "gi");
        stateName = stateName.match(regex)[0]
        return stateName;
    } catch (err) {
        return stateName;
    }
}

function run(previousBlockid, previousStateName, lastInput, intent, serviceName, env) {
	try {
		let match = previousStateName.match(/\[(.*?)\]/);
		let stateNameOrigin = match ? match[1] : previousStateName;
		return {
			botOrigin: serviceName,
			stateOrigin: previousBlockid,
			stateNameOrigin,
			botDestination: `cascata ${env || ""}`.trim(),
			stateDestination: 'welcome',
			lastInput: lastInput || "inatividade",
			intent
		}
	} catch (err) {
		return {
			botOrigin: serviceName,
			stateOrigin: previousBlockid,
			botDestination: `principal ${env || ""}`.trim(),
			stateDestination: 'welcome',
			lastInput: lastInput || intent,
			error: err.message,
		}
	}
}

function run(selectTeam) {
    try {
        let data = new Date();
        let dayMonth = data.getDate();
        let dayWeek = data.getDay();
        let convertHours = (data.getUTCHours() - 3) < 0 ? (data.getUTCHours() - 3) + 24 : (data.getUTCHours() - 3);
        let hours = convertHours < 10 ? '0' + convertHours : convertHours;
        let minutes = data.getMinutes() < 10 ? '0' + data.getMinutes() : data.getMinutes();
        let hoursAndMinutes = hours + ":" + minutes;
        let selectedChannel = selectTeam;
        let channel = selectedChannel == "default" ? 0 : 0
        let weekDefault = { "start": "09:00", "end": "17:59" };
        let weekDeNotFound = { "start": "09:00", "end": "17:59" };
        let objRules = {
            "rules": [
                { "default":  [null, weekDefault, weekDefault, weekDefault, weekDefault, null, null] }
            ]
        }
        if (!objRules.rules[channel][selectedChannel]) {

            if (dayWeek === 0 || dayWeek === 6) {
             return false;
            } else {

            return hoursAndMinutes >= weekDeNotFound.start && hoursAndMinutes <= weekDeNotFound.end;

            }
        } else {
            let resultDay = objRules.rules[channel][selectedChannel][dayWeek];
            return hoursAndMinutes >= resultDay.start && hoursAndMinutes <= resultDay.end;
        }
    }
    catch (e) {
        return e;
    }
}

