// Blocklist common values.
const CPF_BLOCKLIST = [
    '00000000000',
    '11111111111',
    '22222222222',
    '33333333333',
    '44444444444',
    '55555555555',
    '66666666666',
    '77777777777',
    '88888888888',
    '99999999999',
    '12345678909'
];

function run(input) {
    try {
        let inputObject = cpfTreatment(input);

        return inputObject;
    } catch (e) {
        return { 
            input: "input inesperado",
            valid: "invalido"
        };
    }
}

const cpfTreatment = (strCPF) => {
    strCPF = strCPF.replace(/\D+/g, "");

    // CPF must have 11 chars
    if (strCPF.length !== 11) {
        return { 
            input: "input inesperado",
            valid: "invalido"
        };
    }

    // CPF can't be blocklisted
    if (CPF_BLOCKLIST.includes(strCPF)) {
        return { 
            input: "input inesperado",
            valid: "invalido"
        };
    }

    let numbers = strCPF.substr(0, 9);
    numbers += verifierDigitCpf(numbers);
    numbers += verifierDigitCpf(numbers);

    return numbers.substr(-2) === strCPF.substr(-2) ?  numbers  : { input: "input inesperado", valid: "invalido" };
};

const verifierDigitCpf = (digits) => {
    const numbers = digits
        .split('')
        .map(num => {
            return parseInt(num, 10);
        });

    const modulus = numbers.length + 1;
    const multiplied = numbers.map((num, index) => num * (modulus - index));
    const mod = multiplied.reduce((buffer, num) => buffer + num) % 11;

    return (mod < 2 ? 0 : 11 - mod);
};

function run(documento) {
    documento = documento.replace(/\D/g, '');
    if (documento.length == 11) {
        documento = ("000000000" + documento).slice(-11);
        return verifyDoc()["validCpf"](documento);
    } else {
        return verifyDoc()["validCnpj"](documento);
    }

}
function verifyDoc(documento) {
    return {
        validCpf: function (cpf) {
            if (!cpf || cpf.length != 11
                || cpf == "00000000000"
                || cpf == "11111111111"
                || cpf == "22222222222"
                || cpf == "33333333333"
                || cpf == "44444444444"
                || cpf == "55555555555"
                || cpf == "66666666666"
                || cpf == "77777777777"
                || cpf == "88888888888"
                || cpf == "99999999999")
                return false
            var soma = 0
            var resto
            for (var i = 1; i <= 9; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (11 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(9, 10))) return false
            soma = 0
            for (var i = 1; i <= 10; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (12 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(10, 11))) return false
            return true
        },
        validCnpj: function (cnpj) {
            if (!cnpj || cnpj.length != 14
                || cnpj == "00000000000000"
                || cnpj == "11111111111111"
                || cnpj == "22222222222222"
                || cnpj == "33333333333333"
                || cnpj == "44444444444444"
                || cnpj == "55555555555555"
                || cnpj == "66666666666666"
                || cnpj == "77777777777777"
                || cnpj == "88888888888888"
                || cnpj == "99999999999999")
                return false
            var tamanho = cnpj.length - 2
            var numeros = cnpj.substring(0, tamanho)
            var digitos = cnpj.substring(tamanho)
            var soma = 0
            var pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            var resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(0)) return false;
            tamanho = tamanho + 1
            numeros = cnpj.substring(0, tamanho)
            soma = 0
            pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(1)) return false
            return true;
        }
    }
}

