function run(nomeGerente, result) {

    try {
        result = JSON.parse(result);

        if (result.status !== "success") {
            return nomeGerente;
        }

        let resultStatus = result.resource.items.filter(function (e) {
            return e.teams.includes(nomeGerente) && e.status === 'Online';
        })

        if(resultStatus.length > 0){
            return nomeGerente
        }
    } catch (error) {    
    }

    return nomeGerente
}

function run(retornoAPI, atendente) {
  try {
    const objeto = JSON.parse(retornoAPI);
    const { total, items } = objeto.resource;
    let atendenteOnline = false;
    if (total > 0) {
      atendenteOnline =
        items.find(
          (item) => item.name === atendente && item.agentsOnline > 0
        ) != undefined;
    }
    return atendenteOnline;
  } catch (error) {
    return false;
  }
}

