// Função para verificar se a data é um sábado (6) ou domingo (0)
function isWeekend(date) {
  return date.getDay() === 6 || date.getDay() === 0;
}

// Função para verificar se a data é um feriado nacional (adicionar feriados conforme necessário)
function isHoliday(date) {
  // Adicione aqui a lista de feriados no formato 'MM/DD'
  const holidays = [
    '01/01', // Ano Novo
    '04/21', // Dia de Tiradentes
    '05/01', // Dia do Trabalhador
    '09/07', // Dia da Independência
    '10/12', // Nossa Senhora Aparecida
    '11/02', // Dia de Finados
    '11/15', // Proclamação da República
    '12/25', // Natal
  ];

  const month = date.getMonth() + 1; // getMonth() retorna valores de 0 a 11
  const day = date.getDate();
  const dateString = `${month.toString().padStart(2, '0')}/${day.toString().padStart(2, '0')}`;

  return holidays.includes(dateString);
}

// Função para obter o próximo dia útil às 9h da manhã
function getNextBusinessDay(currentDate = new Date(), hours = 9) {
  // Verifica se o dia atual é fim de semana ou feriado
  if (isWeekend(currentDate) || isHoliday(currentDate)) {
    // Avança para o próximo dia e chama a função recursivamente
    const nextDay = new Date(currentDate);
    nextDay.setDate(currentDate.getDate() + 1);
    return getNextBusinessDay(nextDay, hours);
  }

  // Define a hora para 9h da manhã
  currentDate.setHours(hours, 0, 0, 0);

  // Formata a data como uma string no formato "YYYY-MM-DD HH:mm:ss"
  const dateString = currentDate.toISOString() //.slice(0, 19).replace('T', ' ');

  return dateString;
}

function run () {
	const nextBusinessDayAt9AM = getNextBusinessDay();
	return nextBusinessDayAt9AM;
}

