function run(nome) {
    nome = nome.replace(/(O?\s*Meu\s*nome\s*((completo\s*)?[ée]h?)?|Nome:?)/gi, '');
    //Remove pontos e espaços em branco
    nome = nome.replace(/(\.)/gi, ' ');
    nome = nome.replace(/(\s+)/gi, ' ');
    nome = nome.trim();

    var split = nome.split(' ');
    if (split[1] == undefined || split[1] == null) {
        return "input inesperado";
    }

    //valida se as palavras tem pelo menos 2 letras
    for (key in split) {
        // verifica se key é um numero pois o Split traz no key infos como "remove" e "removeItem"
        if (key < 10) {
            if (split[key].length < 2) {
                return "input inesperado";
            }
        }
    }
    //valida se tem 4 caracteres iguais juntos
    for (var i = 0; i < nome.length - 3; i++) {
        if (nome[i] == nome[i + 1] && nome[i] == nome[i + 2] && nome[i] == nome[i + 3]) {
            return "input inesperado";
        }
    }

    //remove os espaços para o teste de caracteres
    const WHITE_SPACES = RegExp("(\\s+)", "gi");
    nomeSemEspacos = nome.replace(WHITE_SPACES, "");

    //valida se tem caracteres especiais
    const INVALID_CARACTERES = RegExp("[^a-záéíóúõãüöäëôâêîûç]", "gi");
    if (!INVALID_CARACTERES.test(nomeSemEspacos))
        return nome;
    else return "input inesperado";
}

