    let month = hoje.getMonth() + 1;
    const newDate = `${day}/${month < 10 ? '0' + month : month}/${hoje.getFullYear()}-${hoje.getHours() < 10 ? '0' + hoje.getHours() : hoje.getHours()}:${hoje.getMinutes()}:${hoje.getSeconds() < 10 ? '0' + hoje.getSeconds() : hoje.getSeconds()}`
    const dateForm = newDate
    return dateForm; 

}

// Blocklist common values.
const CPF_BLOCKLIST = [
    '00000000000000',
    '11111111111111',
    '22222222222222',
    '33333333333333',
    '44444444444444',
    '55555555555555',
    '66666666666666',
    '77777777777777',
    '88888888888888',
    '99999999999999',
    '12345678909012'
];

function run(input) {
    try {
        let inputObject = cpfTreatment(input);

        return inputObject;
    } catch (e) {
        return {
            input: "input inesperado",
            valid: "invalido"
        };
    }
}

const cpfTreatment = (strCPF) => {
    strCPF = strCPF.replace(/\D+/g, "");

    // CPF must have 11 chars
    if (strCPF.length != 14) {
        return {
            input: "input inesperado",
            valid: "invalido"
        };
    }

    // CPF can't be blocklisted
    if (CPF_BLOCKLIST.includes(strCPF)) {
        return {
            input: "input inesperado",
            valid: "invalido"
        };
    }

    let numbers = strCPF.substr(0, 12);
    numbers += verifierDigitCpf(numbers);
    numbers += verifierDigitCpf(numbers);

    return numbers.substr(-2) === strCPF.substr(-2) ? { input: numbers, valid: "valido" } : { input: "input inesperado", valid: "invalido" };
};

const verifierDigitCpf = (digits) => {
    let index = 2;
    const reverse = digits.split('').reduce((buffer, num) => {
        return [parseInt(num, 10)].concat(buffer);
    }, []);

    const sum = reverse.reduce((buffer, num) => {
        buffer += num * index;
        index = (index === 9 ? 2 : index + 1);
        return buffer;
    }, 0);

    const mod = sum % 11;

    return (mod < 2 ? 0 : 11 - mod);
};

