function run(user_id) {
    let extracted_phone = user_id
        .split('@')[0]
        .substr(2);
    if (extracted_phone.length == 10)
        extracted_phone = extracted_phone
            .substr(0, 2)
            .concat('9', extracted_phone.substr(2));
    return extracted_phone;
}

function run(clientId, grantType, clientSecret) {

    const URL = `client_id=${clientId}&grant_type=${grantType}&client_secret=${clientSecret}`
    return URL;

}

function run(documento) {
    documento = documento.replace(/\D/g, '');
    if (documento.length == 11) {
        documento = ("000000000" + documento).slice(-11);
        return verifyDoc()["validCpf"](documento);
    } else {
        return verifyDoc()["validCnpj"](documento);
    }

}
function verifyDoc(documento) {
    return {
        validCpf: function (cpf) {
            if (!cpf || cpf.length != 11
                || cpf == "00000000000"
                || cpf == "11111111111"
                || cpf == "22222222222"
                || cpf == "33333333333"
                || cpf == "44444444444"
                || cpf == "55555555555"
                || cpf == "66666666666"
                || cpf == "77777777777"
                || cpf == "88888888888"
                || cpf == "99999999999")
                return false
            var soma = 0
            var resto
            for (var i = 1; i <= 9; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (11 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(9, 10))) return false
            soma = 0
            for (var i = 1; i <= 10; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (12 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(10, 11))) return false
            return true
        },
        validCnpj: function (cnpj) {
            if (!cnpj || cnpj.length != 14
                || cnpj == "00000000000000"
                || cnpj == "11111111111111"
                || cnpj == "22222222222222"
                || cnpj == "33333333333333"
                || cnpj == "44444444444444"
                || cnpj == "55555555555555"
                || cnpj == "66666666666666"
                || cnpj == "77777777777777"
                || cnpj == "88888888888888"
                || cnpj == "99999999999999")
                return false
            var tamanho = cnpj.length - 2
            var numeros = cnpj.substring(0, tamanho)
            var digitos = cnpj.substring(tamanho)
            var soma = 0
            var pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            var resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(0)) return false;
            tamanho = tamanho + 1
            numeros = cnpj.substring(0, tamanho)
            soma = 0
            pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(1)) return false
            return true;
        }
    }
}

function run(calendarDatetime, inputMessage, inputContent, broadcast, identity) {
    const date = new Date(calendarDatetime);

    const dataFormatada = dataAtualFormatada(date);
    let metadataCampanha = metadata(inputMessage);
    const origem = origemCampanha(inputContent, metadataCampanha, broadcast);
    const canal = origemCanal(identity);
    metadataCampanha = metadataCampanha != "" ? metadataCampanha : "nao possui campanha";

    const trackOrigemCampanha = `${dataFormatada} | ${identity} | ${metadataCampanha} | ${canal} | ${origem} | ${inputContent}`

    return { 
        trackOrigemCampanha,
        canal,
        origem: origem.split(" |")[0],
        dataFormatada
    };
}

function dataAtualFormatada(data) {
    const dia = data.getDate().toString();
    const diaF = (dia.length == 1) ? '0' + dia : dia;
    const mes = (data.getMonth() + 1).toString(); //+1 pois no getMonth Janeiro começa com zero.
    const mesF = (mes.length == 1) ? '0' + mes : mes;
    const anoF = data.getFullYear();
    const hora = data.getHours().toString();
    const horaF = (hora.length == 1) ? '0' + hora : hora;
    const min = data.getMinutes().toString();
    const minF = (min.length == 1) ? '0' + min : min;
    const seg = data.getSeconds().toString();
    const segF = (seg.length == 1) ? '0' + seg : seg;

    return `${diaF}/${mesF}/${anoF} ${horaF}:${minF}:${segF}`;
}

function metadata(inputMessage) {
    try {
        let inputMessageFormatted = JSON.parse(inputMessage);
        // let inputMessageFormatted = inputMessage;
        let metadata = inputMessageFormatted.metadata;

        if (metadata && metadata['#wa.referral.headline']) {
            const metadataCampanha = `${metadata['#wa.referral.source_id']} | ${metadata['#wa.referral.source_url']} | ${metadata['#wa.referral.headline']} | ${metadata['#wa.referral.body']} | ${metadata['#wa.referral.source_type']} | ${metadata['#wa.referral.image.id']}`
            return metadataCampanha;
        } else {
            const metadataCampanha = ""
            return metadataCampanha;
        }
    } catch (err) {
        const metadataCampanha = "";
        return metadataCampanha;
    }
}

function origemCampanha(inputContent, metadata, broadcast) {
    try {
        const sourceUrl = metadata;
        if (sourceUrl != "") {
            return "Campanha"
        } else if(broadcast){
            return `broadcast | ${broadcast}`
        }
        return "organico"
    } catch (err) {
        return "organico"
    }
}

function origemCanal(identity) {
    try {
        const source = identity.split('@')[1].split('.')[0];
        switch (source) {
            case 'wa':
                return "whatsapp";
                break;
            case 'businesschat':
                return "businesschat";
                break;
            case '0mn':
                return "blipchat";
                break;
            case 'messenger':
                return "messenger";
                break;
            case 'telegram':
                return "telegram";
                break;
            case 'instagram':
                return "instagram";
                break;
            case 'mailgun':
                return "email";
                break;
            case 'workplace':
                return "workchat";
                break;
            case 'tunnel':
                return "whatsapp";
                break;
            case 'abs':
                return "teams";
                break;
            default:
                return "new"
                break;
        }

    } catch (error) {
        return false;
    }
}

function run(state, serviceName) {
	try {
		const { botDestination, stateDestination } = JSON.parse(state)
		if (stateDestination === 'desk:b13a0f3b-d382-4f27-ac21-541cfe7c7f2f') {
			return 'desk:b13a0f3b-d382-4f27-ac21-541cfe7c7f2f'
		}
		if (stateDestination.match(/^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/) && botDestination === serviceName) {
			return stateDestination
		}
		return false
	} catch (err) {
		return false
	}
}

