function DateBR(dataTicket) {
    Date.prototype.addHours = function (value) {
        this.setHours(this.getHours() + value);
    }
    var hoje = new Date(dataTicket);
    hoje.addHours(0);
    let day = hoje.getDate() < 10 ? '0' + hoje.getDate() : hoje.getDate();
    let month = hoje.getMonth() + 1;
    const newDate = `${day}/${month < 10 ? '0' + month : month}/${hoje.getFullYear()} - ${hoje.getHours() < 10 ? '0' + hoje.getHours() : hoje.getHours()}:${hoje.getMinutes()}:${hoje.getSeconds() < 10 ? '0' + hoje.getSeconds() : hoje.getSeconds()}`
    return newDate;
}

