    let utc_timestamp = Date.UTC(
        now.getUTCFullYear(),
        now.getUTCMonth(),
        now.getUTCDate(),
        now.getUTCHours(),
        now.getUTCMinutes(),
        now.getUTCSeconds(),
        now.getUTCMilliseconds()
    );

    let dataCompleta =  new Date(utc_timestamp + offset * 3600 * 1000);
    let dataHora = dataCompleta.getHours() + 3;
    return dataHora;
}

