settings.source:
function run(documento) {
    documento = documento.replace(/\D/g, '');
    if (documento.length == 11) {
        documento = ("000000000" + documento).slice(-11);
        return verifyDoc()["validCpf"](documento);
    } else {
        return verifyDoc()["validCnpj"](documento);
    }

}
function verifyDoc(documento) {
    return {
        validCpf: function (cpf) {
            if (!cpf || cpf.length != 11
                || cpf == "00000000000"
                || cpf == "11111111111"
                || cpf == "22222222222"
                || cpf == "33333333333"
                || cpf == "44444444444"
                || cpf == "55555555555"
                || cpf == "66666666666"
                || cpf == "77777777777"
                || cpf == "88888888888"
                || cpf == "99999999999")
                return false
            var soma = 0
            var resto
            for (var i = 1; i <= 9; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (11 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(9, 10))) return false
            soma = 0
            for (var i = 1; i <= 10; i++)
                soma = soma + parseInt(cpf.substring(i - 1, i)) * (12 - i)
            resto = (soma * 10) % 11
            if ((resto == 10) || (resto == 11)) resto = 0
            if (resto != parseInt(cpf.substring(10, 11))) return false
            return true
        },
        validCnpj: function (cnpj) {
            if (!cnpj || cnpj.length != 14
                || cnpj == "00000000000000"
                || cnpj == "11111111111111"
                || cnpj == "22222222222222"
                || cnpj == "33333333333333"
                || cnpj == "44444444444444"
                || cnpj == "55555555555555"
                || cnpj == "66666666666666"
                || cnpj == "77777777777777"
                || cnpj == "88888888888888"
                || cnpj == "99999999999999")
                return false
            var tamanho = cnpj.length - 2
            var numeros = cnpj.substring(0, tamanho)
            var digitos = cnpj.substring(tamanho)
            var soma = 0
            var pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            var resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(0)) return false;
            tamanho = tamanho + 1
            numeros = cnpj.substring(0, tamanho)
            soma = 0
            pos = tamanho - 7
            for (var i = tamanho; i >= 1; i--) {
                soma += numeros.charAt(tamanho - i) * pos--
                if (pos < 2) pos = 9
            }
            resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
            if (resultado != digitos.charAt(1)) return false
            return true;
        }
    }
}

function run(botId, botName, userName, contactIdentity, phoneNumber, cpf, ticketInfo) {
    let ticket = (ticketInfo != undefined) ? JSON.parse(ticketInfo) : '';
    const {id, sequentialId, team, agentIdentity, tags} = ticket;

    let tag = (tags != undefined && tags != '') ? tags.toString() : '';

    const npsBotOrHuman = botOrHuman({id});
    const ticketInfoOpenDate = ticketOpenDateFormated(ticket);

    const npsExibicao = `${cpf || ''} | ${npsBotOrHuman} | ${id || ''} | ${sequentialId || ''} | ${ticketInfoOpenDate} | ${botId} | ${botName} | ${contactIdentity || ''} | ${userName} | ${phoneNumber} | ${team || ''} | ${agentIdentity || ''} | ${tag || ''}`;
    const npsExibInesperado = `${npsBotOrHuman} | ${id || ''} | ${sequentialId || ''}`;

    return {
        npsExibicao,
        npsExibInesperado
    };
}

function ticketOpenDateFormated(ticket) {
    _ticket = ticket;

    if(_ticket.openDate){
        let date = new Date(_ticket.openDate);
        let day = date.getUTCDate() < 10 ? "0" + date.getUTCDate() : date.getUTCDate();
        let month = (date.getUTCMonth()+1) < 10 ? "0" + (date.getUTCMonth()+1) : (date.getUTCMonth()+1);
        let hour = (date.getUTCHours()-3) < 10 ? "0" + (date.getUTCHours()-3) : (date.getUTCHours()-3);
        let minutes = date.getUTCMinutes() < 10 ? "0" + date.getUTCMinutes() : date.getUTCMinutes();
        let seconds =  date.getUTCSeconds() < 10 ? "0" +  date.getUTCSeconds() :  date.getUTCSeconds();
        let newDate = `${day}/${month}/${date.getUTCFullYear()} ${hour}:${minutes}:${seconds}`;
        return newDate;
    }

    return "";
}

function botOrHuman({id = "bot"}) {
    return id == "bot" ? "bot" : "human";
}

function run(input) {
    if (input >= 0 && input <= 6) {
        return "detratores";
    } else if (input == 7 || input == 8) {
        return "neutros";
    } else if (input == 9 || input == 10){
        return "promotores";
    } else {
        return "outro"
    }
}

function run(identity) {
    let extracted_phone = identity
        .split('@')[0]
        .substr(2);
    if (extracted_phone.length == 10)
        extracted_phone = extracted_phone
            .substr(0, 2)
            .concat('9', extracted_phone.substr(2));
    return extracted_phone;
}

function run(input) {

    let input = parseInt(input);

    return input.toString();

}

// Receive the variables as parameters
function run(score) {
