function run(documento) {
	if (!documento) {
		return 'inatividade'
	}
	documento = String(documento).replace(/\D/g, '');
	return verifyDoc(documento)
}
function verifyDoc(cnpj) {
	if (!cnpj || cnpj.length != 14
		|| cnpj == "00000000000000"
		|| cnpj == "11111111111111"
		|| cnpj == "22222222222222"
		|| cnpj == "33333333333333"
		|| cnpj == "44444444444444"
		|| cnpj == "55555555555555"
		|| cnpj == "66666666666666"
		|| cnpj == "77777777777777"
		|| cnpj == "88888888888888"
		|| cnpj == "99999999999999")
		return 'input inesperado'
	var tamanho = cnpj.length - 2
	var numeros = cnpj.substring(0, tamanho)
	var digitos = cnpj.substring(tamanho)
	var soma = 0
	var pos = tamanho - 7
	for (var i = tamanho; i >= 1; i--) {
		soma += numeros.charAt(tamanho - i) * pos--
		if (pos < 2) pos = 9
	}
	var resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
	if (resultado != digitos.charAt(0)) return 'input inesperado';
	tamanho = tamanho + 1
	numeros = cnpj.substring(0, tamanho)
	soma = 0
	pos = tamanho - 7
	for (var i = tamanho; i >= 1; i--) {
		soma += numeros.charAt(tamanho - i) * pos--
		if (pos < 2) pos = 9
	}
	resultado = soma % 11 < 2 ? 0 : 11 - soma % 11
	if (resultado != digitos.charAt(1)) return 'input inesperado'
	return cnpj;
}

function montaCnpj(baseDocumento, filialCnpj, digitoCpf) {
  let digito = ''
  if (digitoCpf < 10 && digitoCpf > 0) {
    digito = "0" + digitoCpf
  } else {
    digito = digitoCpf
  }

  let doc = Number(baseDocumento) + "000" + Number(filialCnpj) + digito
  while (doc.length < 14) {
    doc = `0${doc}`
  }
  return doc
}

function apiPhone(apiResponse) {
  const { id, codigoSegmento, nomeCliente, baseCpfCnpj, filialCnpj, digitoVerificadorCpfCnpj } = apiResponse
  const contaSelecionada = {
    filaGerente: id,
    segmentoGerente: Number(codigoSegmento) === 17 ? 'emp2' : 'emp1',
    nomeEmpresa: nomeCliente,
    doc: montaCnpj(baseCpfCnpj, filialCnpj, digitoVerificadorCpfCnpj),
    codigoSegmento
  }
  return contaSelecionada
}

function apiGerentes(apiResponse, doc) {
  const { sigla, codigoSegmento, nomeSegmento } = apiResponse
  const contaSelecionada = {
    filaGerente: sigla,
    segmentoGerente: Number(codigoSegmento) === 17 ? 'emp2' : 'emp1',
    nomeEmpresa: nomeSegmento,
    doc,
    codigoSegmento
  }
  return contaSelecionada
}

