function run(...apiStatus) {
	try {
		if (apiStatus.some((status) => !status)) {
			return 'timeout'
		}
		if (apiStatus.some((status) => status == 401)) {
			return 'unauthorized'
		}
		if (apiStatus.some((status) => status != 200)) {
			return 'erro api'
		}
		return 'ok'
	} catch (err) {
		return "erro api"
	}
}
