function run(data) {
	try {
		if (!data) {
			return 'inatividade'
		}
		const regex = /^(\d{2})[\/\-]?(\d{2})[\/\-]?(\d{4})$/;
		if (!regex.test(data)) return 'input inesperado';

		let dia = parseInt(data.substring(0, 2));
		let mes = parseInt(data.substring(3, 5));
		let ano = parseInt(data.substring(6, 10));

		if (dia < 1 || dia > 31) return 'input inesperado';
		if (mes < 1 || mes > 12) return 'input inesperado';
		if ((mes == 4 || mes == 6 || mes == 9 || mes == 11) && dia == 31) return 'input inesperado';
		if (mes == 2) {
			const bissexto = (ano % 4 == 0 && (ano % 100 != 0 || ano % 400 == 0));
			if (dia > 29 || (dia == 29 && !bissexto)) return 'input inesperado';
		}
		return dia.toString().padStart(2, '0') + '/' + mes.toString().padStart(2, '0') + '/' + ano;
	} catch (err) {
		return 'input inesperado'
	}
}

function run(valor, message) {
    try {
        if (!valor) {
            return 'inatividade'
        }
        const messageType = JSON.parse(message).type
        if (messageType !== "text/plain") {
            return 'input inesperado'
        }

        if (!/^\d{1,3}(\.\d{3})*,\d{2}$/.test(valor)) {
            return 'input inesperado';
        }

        const valorNumerico = valor.replace(/\./g, '').replace(',', '.');
        const valorFormatado = parseFloat(valorNumerico).toFixed(2).replace('.', ',');
        return 'R$ ' + valorFormatado;
    } catch (err) {
        return err.message
    }
}

