function run(state, serviceName) {
	try {
		const { botDestination, stateDestination } = JSON.parse(state)

		if (stateDestination.match(/^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/) && botDestination === serviceName) {
			return stateDestination
		}
		return false
	} catch (err) {
		return false
	}
}

function formatPhone(identity) {
    if (identity.split("@")[1] == "wa.gw.msging.net") {
        let resultPhoneWithoutCode = identity.split("@")[0].substring(2, identity.length);
        if (resultPhoneWithoutCode.length < 11) {
            let newArraySplice = resultPhoneWithoutCode.split("");
            let returnNewArraySplice = newArraySplice.splice(2, 0, "9");
            return newArraySplice.join("")
        } else {
            return resultPhoneWithoutCode
        }
    }
    return identity
}

function run(contactPhone) {
    try {
        const identity = formatPhone(contactPhone)
        let telephone = identity.substring(2, identity.length);
        let ddd = identity.substring(0, 2);
        let objectResponse = {
            "ddd": ddd,
            "telephone": telephone
        };
        return objectResponse;
    } catch (err) {
        return false
    }
}

