function run(cpf, exception) {
    const invalidCpfMessage = 'input inesperado';
    try {
        if (!cpf) {
            return 'inatividade'
        }
        if (exception === 'quickReply' || exception === 'skip') {
            if (cpf.match(/tentar (novamente|novamnete|novament|novametne)/i)) {
                return 'tentar novamente'
            }
            if (cpf.match(/encerrar(a|ar|ra|r)/i)) {
                return 'encerrar'
            }
            return invalidCpfMessage
        }

        cpf = cpf.replace(/\D/g, '');
        if (cpf.length !== 11 || /^(.)\1*$/.test(cpf)) {
            return invalidCpfMessage;
        }
        let sum = 0;
        let remainder;
        for (let i = 1; i <= 9; i++) {
            sum += parseInt(cpf.substring(i - 1, i)) * (11 - i);
        }
        remainder = (sum * 10) % 11;
        if ((remainder === 10) || (remainder === 11)) {
            remainder = 0;
        }
        if (remainder !== parseInt(cpf.substring(9, 10))) {
            return invalidCpfMessage;
        }
        sum = 0;
        for (let i = 1; i <= 10; i++) {
            sum += parseInt(cpf.substring(i - 1, i)) * (12 - i);
        }
        remainder = (sum * 10) % 11;
        if ((remainder === 10) || (remainder === 11)) {
            remainder = 0;
        }
        if (remainder !== parseInt(cpf.substring(10, 11))) {
            return invalidCpfMessage;
        }
        return cpf;
    } catch (err) {
        return invalidCpfMessage;
    }
}

function run(message, contas) {
	try {
		if (!message) {
			return 'inatividade'
		}
		if (isNaN(message)) {
			return 'input inesperado'
		}
		const index = Number(message) - 1
		const parsedContas = JSON.parse(contas)
		const contaSelecionada = parsedContas.contas[index]
		if (!contaSelecionada) {
			return 'input inesperado'
		}
		return contaSelecionada
	} catch (err) {
		return 'input inesperado'
	}
}

