const run = (input) => {
    try {
        var cleanedInput = input.normalize("NFD").replace(/[^\w\s]/gi, '').toLowerCase();
        const dicts = {
            "\\b(0?1|um|muito f[√°a]cil)\\b": "Muito F√°cil",
            "\\b(0?2|dois|f[√°a]cil)\\b": "F√°cil",
