			"\\b(0?5|cinco|muito dif[i√≠]cil)\\b": "Muito Dif√≠cil",
			"\\b(0?4|quatro|dif[i√≠]cil)\\b": "Dif√≠cil",
        };

        const match = Object.keys(dicts).find(key => (cleanedInput.match(new RegExp(key, "gi"))));

        return dicts[match] || "input inesperado";
    } catch (e) {
        return "input inesperado";
    }
}

