function run(inputContent, pesquisaBigID) {
	try {
		const parsedPesquisa = JSON.parse(pesquisaBigID).questionList
		const answer = ''
		if (isNaN(inputContent)) {
			answer = parsedPesquisa[0].alternativas.find((alternativa) => alternativa.toLowerCase() == inputContent.toLowerCase())
		} else {
			answer = parsedPesquisa[0].alternativas[Number(inputContent) - 1]
		}

		if (answer) {
			return answer
		}
		return 'input inesperado'
	} catch (err) {
		return 'input inesperado'
	}
}

