function run(bigIdResponses, userResponse) {
	try {
		if (!bigIdResponses) {
			return [userResponse]
		}
		const parsedBigIdResponses = JSON.parse(bigIdResponses)
		parsedBigIdResponses.push(userResponse)
		return parsedBigIdResponses
	} catch (err) {
		return bigIdResponses
	}
}

