**/
function run(bodyQuestion) {
	try {
		const parsedQuestions = JSON.parse(bodyQuestion)

		if (parsedQuestions.Questions.length > 0) {
			const questionList = parsedQuestions.Questions.map(({ Question, Answers }) => ({
				questao: Question,
				alternativas: Answers
			}))
			return {
				questionList,
				quantidadePerguntas: questionList.length,
			}
		}
		return false
	} catch (err) {
		return false
	}
}

