	salesPaymentTypeStringfy = salesPaymentTypeStringfy.replace(`debito`,`DÃ©bito Ã  vista`).replace(`creditoAVista`, `CrÃ©dito Ã  vista`).replace(`creditoParcelado`, `CrÃ©dito parcelado`)
	return salesPaymentTypeStringfy; 
}

function run(valor) {
  try {
    const pontos = (valor.match(/\./g) || []).length;
    const virgulas = (valor.match(/\,/g) || []).length;

    if (pontos > 1 || virgulas > 1) {
      valor = valor.replace(/\./g, '').replace(',', '.');
    } else if ((valor.split('.')[1] || []).length == 2 && pontos == 1) {
      valor = valor.replace(',', '');
    } else if ((valor.split(',')[1] || []).length == 2 && virgulas == 1) {
      valor = valor.replace('.', '').replace(',', '.');
    } else {
      valor = valor.replace('.', '').replace(',', '.');
    }

    valor = parseFloat(valor);
    let valorFormatado = valor.toFixed(2).replace('.', ',');
    valorFormatado = valorFormatado.replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1.');
    return valorFormatado;
  }
  catch (e) {
    return valor;
  }
};

function run(bandeira, bodySimulacao,salesOrder) {
	let bandeira = bandeira.toLowerCase();
	let bodySimulacao = JSON.parse(bodySimulacao);
	const formatValue = (valor) => {
		return `${
			parseFloat(valor)
				.toFixed(2)
				.replace(".", ",")
				.replace(/\B(?=(\d{3})+(?!\d))/g, ".")}`;
	}
	bodySimulacao = bodySimulacao.data[salesOrder].modalidades[0].bandeiras
	bodySimulacao = bodySimulacao.filter(bodySimulacao => (bodySimulacao.descricao == bandeira))
	bodySimulacao = bodySimulacao[0].simulacoes[0]
	bodySimulacao.valorFinal = formatValue(bodySimulacao.valorFinal)
	bodySimulacao.valorTaxaTotal = formatValue(bodySimulacao.valorTaxaTotal)

	return JSON.stringify(bodySimulacao);
}

function run(salesOrder, salesSimulation, salesValueStringfy) {
