		salesSimulation = JSON.parse(salesSimulation);
		if (salesOrder == "receber") {
			return `Cobrando: R$${salesSimulation.valorFinal}\nVocÃª receberÃ¡: R$${salesValueStringfy}`
		}
		else {
			return `Cobrando: R$${salesValueStringfy}\nVocÃª receberÃ¡: R$${salesSimulation.valorFinal}`
		}
	}
	catch (e) {
		return false;
	}
}

