/**
 * @typedef {Object} MenuOption
 * @property {number} posicao - PosiÃ§Ã£o que o botÃ£o deve ser inserido no menu
 * @property {string} title - TÃ­tulo do botÃ£o
 * @property {string} description - DescriÃ§Ã£o do botÃ£o
 */
/**
 * @summary Essa funÃ§Ã£o adiciona uma opÃ§Ã£o (botÃ£o) no menu de acordo com a posiÃ§Ã£o informada e reordena todos os botÃµes.
 * @param {MenuOption} option - Objeto contendo a posiÃ§Ã£o, tÃ­tulo e descriÃ§Ã£o do botÃ£o
 * @example adicionaOpcaoNoMenu({ posicao: 2, title: 'Conta Corrente', description: 'Extratos, Fazer pix, Antecipar saldo, Falar sobre conta, entre outros.' })
 */
function adicionaOpcaoNoMenu({ posicao, title, description }) {
    if (posicao > menuOptions.length) {
        posicao = Number(menuOptions[menuOptions.length - 1].id) + 1
    }
    if (menuOptions.find(option => option.id == posicao)) {
        quantidadeItens = menuOptions.length
        for (let i = Number(posicao - 1); i < quantidadeItens; i++) {
            menuOptions[i].id = (i + 2).toString()
        }
        menuOptions.push({
            id: posicao.toString(),
            title: title.slice(0, 24),
            description: description.slice(0, 72) || ''
        })
    } else {
        menuOptions.push({
            id: posicao.toString(),
            title: title.slice(0, 24),
            description: description.slice(0, 72)
        })
    }
    menuOptions.sort((a, b) => a.id - b.id)
}

/**
 * @typedef {Object} MenuOptionUpdate
 * @property {string} parametroDeBusca - TÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja atualizar
 * @property {string} title - Novo tÃ­tulo do botÃ£o
 * @property {string} description - Nova descriÃ§Ã£o do botÃ£o
 */

/**
 * @summary Essa funÃ§Ã£o atualiza o tÃ­tulo e descriÃ§Ã£o de uma opÃ§Ã£o (botÃ£o) do menu.
 * @param {MenuOptionUpdate} option - Objeto contendo o tÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja atualizar, novo tÃ­tulo e nova descriÃ§Ã£o do botÃ£o
 * @example atualizaOpcaoMenu({ parametroDeBusca: 'Conta Corrente', title: 'Conta Corrente', description: 'Extratos, Fazer pix, Antecipar saldo, Falar sobre conta, entre outros.' })
 */
function atualizaOpcaoMenu({ parametroDeBusca, title, description }) {
    // Parametro de busca Ã© o title ou description da opÃ§Ã£o que deseja atualizar
    const opcao = menuOptions.find(option => option.title === parametroDeBusca || option.description === parametroDeBusca)
    if (!opcao) {
        return
    }
    menuOptions.find(option => option.id === opcao.id).title = title.slice(0, 24)
    menuOptions.find(option => option.id === opcao.id).description = description.slice(0, 72) || ''
}
/**
 * @typedef {Object} PermissionCheck
 * @property {string} tituloDoBotao - TÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja verificar
 * @property {Array.<string>} permissoes - PermissÃµes do usuÃ¡rio (usar o array de permissÃµes da conta selecionada)
 * @property {string} permissoesNecessarias - PermissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o
 */

/**
 * @summary Essa funÃ§Ã£o verifica se o usuÃ¡rio possui a permissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o no menu.
 * @param {PermissionCheck} permission - Objeto contendo o tÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja verificar, permissÃµes do usuÃ¡rio e permissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o
 * @example verificaPermissao({ tituloDoBotao: 'Conta Corrente', permissoes: ['extratoDeContaCorrente'], permissoesNecessarias: 'extratoDeContaCorrente' })
 */
function verificaPermissao({ tituloDoBotao, permissoes, permissoesNecessarias }) {
    const opcao = menuOptions.find(option => option.title === tituloDoBotao || option.description === tituloDoBotao)
    if (permissoes.includes(permissoesNecessarias)) {
        return
    }
    if (!opcao) {
        return
    }
    const index = menuOptions.indexOf(opcao)
    menuOptions.splice(index, 1)
}

function removeOpcaoDoMenu({ tituloDoBotao }) {
    const opcao = menuOptions.find(option => option.title === tituloDoBotao || option.description === tituloDoBotao)
    if (!opcao) {
        return
    }
    const index = menuOptions.indexOf(opcao)
    menuOptions.splice(index, 1)
}

