/**
 * @typedef {Object} MenuOption
 * @property {number} posicao - PosiÃ§Ã£o que o botÃ£o deve ser inserido no menu
 * @property {string} title - TÃ­tulo do botÃ£o
 * @property {string} description - DescriÃ§Ã£o do botÃ£o
 */
/**
 * @summary Essa funÃ§Ã£o adiciona uma opÃ§Ã£o (botÃ£o) no menu de acordo com a posiÃ§Ã£o informada e reordena todos os botÃµes.
 * @param {MenuOption} option - Objeto contendo a posiÃ§Ã£o, tÃ­tulo e descriÃ§Ã£o do botÃ£o
 * @example adicionaOpcaoNoMenu({ posicao: 2, title: 'Conta Corrente', description: 'Extratos, Fazer pix, Antecipar saldo, Falar sobre conta, entre outros.' })
 */
function adicionaOpcaoNoMenu({ posicao, title, description }) {
    if (posicao > menuOptions.length) {
        posicao = Number(menuOptions[menuOptions.length - 1].id) + 1
    }
    if (menuOptions.find(option => option.id == posicao)) {
        quantidadeItens = menuOptions.length
        for (let i = Number(posicao - 1); i < quantidadeItens; i++) {
            menuOptions[i].id = (i + 2).toString()
        }
        menuOptions.push({
            id: posicao.toString(),
            title: title.slice(0, 24),
            description: description.slice(0, 72) || ''
        })
    } else {
        menuOptions.push({
            id: posicao.toString(),
            title: title.slice(0, 24),
            description: description.slice(0, 72)
        })
    }
    menuOptions.sort((a, b) => a.id - b.id)
}

/**
 * @typedef {Object} MenuOptionUpdate
 * @property {string} parametroDeBusca - TÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja atualizar
 * @property {string} title - Novo tÃ­tulo do botÃ£o
 * @property {string} description - Nova descriÃ§Ã£o do botÃ£o
 */

/**
 * @summary Essa funÃ§Ã£o atualiza o tÃ­tulo e descriÃ§Ã£o de uma opÃ§Ã£o (botÃ£o) do menu.
 * @param {MenuOptionUpdate} option - Objeto contendo o tÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja atualizar, novo tÃ­tulo e nova descriÃ§Ã£o do botÃ£o
 * @example atualizaOpcaoMenu({ parametroDeBusca: 'Conta Corrente', title: 'Conta Corrente', description: 'Extratos, Fazer pix, Antecipar saldo, Falar sobre conta, entre outros.' })
 */
function atualizaOpcaoMenu({ parametroDeBusca, title, description }) {
    // Parametro de busca Ã© o title ou description da opÃ§Ã£o que deseja atualizar
    const opcao = menuOptions.find(option => option.title === parametroDeBusca || option.description === parametroDeBusca)
    if (!opcao) {
        return
    }
    menuOptions.find(option => option.id === opcao.id).title = title.slice(0, 24)
    menuOptions.find(option => option.id === opcao.id).description = description.slice(0, 72) || ''
}
/**
 * @typedef {Object} PermissionCheck
 * @property {string} tituloDoBotao - TÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja verificar
 * @property {Array.<string>} permissoes - PermissÃµes do usuÃ¡rio (usar o array de permissÃµes da conta selecionada)
 * @property {string} permissoesNecessarias - PermissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o
 */

/**
 * @summary Essa funÃ§Ã£o verifica se o usuÃ¡rio possui a permissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o no menu.
 * @param {PermissionCheck} permission - Objeto contendo o tÃ­tulo ou descriÃ§Ã£o da opÃ§Ã£o que deseja verificar, permissÃµes do usuÃ¡rio e permissÃ£o necessÃ¡ria para exibir a opÃ§Ã£o
 * @example verificaPermissao({ tituloDoBotao: 'Conta Corrente', permissoes: ['extratoDeContaCorrente'], permissoesNecessarias: 'extratoDeContaCorrente' })
 */
function verificaPermissao({ tituloDoBotao, permissoes, permissoesNecessarias }) {
    const opcao = menuOptions.find(option => option.title === tituloDoBotao || option.description === tituloDoBotao);

    if (!opcao) {
        return;
    }

    const hasPermission = permissoesNecessarias.some(necessaria => permissoes.includes(necessaria));

    if (hasPermission) {
        return;
    }

    const index = menuOptions.indexOf(opcao);
    if (index > -1) {
        menuOptions.splice(index, 1);
    }
}

function removeOpcaoDoMenu({ tituloDoBotao }) {
    const opcao = menuOptions.find(option => option.title === tituloDoBotao || option.description === tituloDoBotao)
    if (!opcao) {
        return
    }
    const index = menuOptions.indexOf(opcao)
    menuOptions.splice(index, 1)
}

function removeDescricaoDoBotao({ tituloDoBotao, parteASerRemovida, listaPermissoes, permissaoNecessaria }) {
    if (listaPermissoes.includes(permissaoNecessaria)) {
        return
    }
    const opcao = menuOptions.find(option => option.title === tituloDoBotao || option.description === tituloDoBotao)
    if (!opcao) {
        return
    }
    const index = menuOptions.indexOf(opcao)
    menuOptions[index].description = menuOptions[index].description.replace(parteASerRemovida, '')
}

/**
 * @typedef {Object} ContaSelecionada
 * @property {number} codigoCliente
 * @property {string} baseDocumento
 * @property {string} agencia
 * @property {string} agenciaCadastroBacen
 * @property {string} conta
 * @property {(boolean|string)} contaCorrenteSPD
 * @property {string} canal
 * @property {string} shortname
 * @property {string} usuario
 * @property {number} codigoSegmento
 * @property {string} nivel
 * @property {string} nomeEmpresa
 * @property {string} tipoPessoa
 * @property {string} doc
 * @property {string} nomeCompleto
 * @property {string} primeiroNome
 * @property {string} filaGerente
 * @property {Array.<string>} permissoes
 * @property {string} cpfAppKey
 * @property {(boolean|string)} ec
 * @property {string} segmentoGerente
 */

/**
 * @param {ContaSelecionada} contaSelecionada - Objeto contendo as informaÃ§Ãµes da conta selecionada
 * @description Dentro do try, defina todas as regras de exibiÃ§Ã£o, utilizando as funÃ§Ãµes disponiveis: "verificaPermissao", "atualizaOpcaoMenu", "adicionaOpcaoNoMenu"
 */
function run(contaSelecionada) {
    try {
        const { segmentoGerente, permissoes } = JSON.parse(contaSelecionada)

        if (!segmentoGerente) {
            removeOpcaoDoMenu({ tituloDoBotao: 'Outras credenciadoras' })
        }

        verificaPermissao({ tituloDoBotao: 'AntecipaÃ§Ã£o AutomÃ¡tica', permissoesNecessarias: ['habilitarAntecipAutomatica'], permissoes })
        verificaPermissao({ tituloDoBotao: 'AntecipaÃ§Ã£o Eventual', permissoesNecessarias: ['habilitarAntecipEventual'], permissoes })
        verificaPermissao({ tituloDoBotao: 'Cancelamento', permissoesNecessarias: ['cancelarAntecipAutomatica'], permissoes })
        return {
            "recipient_type": "individual",
            "type": "interactive",
            "interactive": {
                "type": "list",
                "header": {
                    "type": "text",
                    "text": ""
                },
                "body": {
                    "text": `Sobre *Erros gerais*, selecione no menu abaixo a opÃ§Ã£o desejada ou, se preferir, digite o que precisa. 
Como neste exemplo: '*Erro de comunicaÃ§Ã£o*'.`
                },
                "action": {
                    "button": "Escolha uma opÃ§Ã£o",
                    "sections": [
                        {
                            "title": "",
                            "rows": menuOptions
                        }
                    ]
                }
            }
        }
    } catch (err) {
        return {
            error: err.message,
            exceptionType: 'reiniciarFluxo',
            dynamicContent: {
                "type": "text/plain",
                "content": `Estamos fazendo alguns ajustes no sistema e alguns recursos podem estar temporariamente indisponÃ­veis. Em breve, estaremos de volta ao normal.`
            }
        }
    }
}

function escapeRegExp(string) {
  return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); // $& means the whole matched string
}

function botao(inputUsuario, dynamicContent) {
  const posicoes = {
      "1": "primeir[oa]|u[mn]",
      "2": "segund[ao]|do[ie]s",
      "3": "terceir[ao]|tr[eÃª]s"
  }
  try {
      if (!inputUsuario) return 'inatividade'
      const regexDictionary = {}
      const { buttons } = JSON.parse(dynamicContent).interactive.action
      buttons.forEach((button, index) => {
          const key = `^(${index + 1}|${posicoes[index + 1]}|${escapeRegExp(button.reply.title.normalize("NFD").replace(/[\u0300-\u036f]/g, "").toLowerCase())})$`
          regexDictionary[key] = button.reply.title
      })
      for (let regex in regexDictionary) {
          if (new RegExp(regex, 'i').test(inputUsuario.normalize("NFD").replace(/[\u0300-\u036f]/g, ""))) {
              return regexDictionary[regex]
          }
      }
      return 'input inesperado'
  } catch (err) {
      return 'input inesperado'
  }
}

