function run(botId, botName, userName, contactIdentity,  phoneNumber, cpf, ticketInfo) {
    let ticket = (ticketInfo != undefined) ? JSON.parse(ticketInfo) : '';
    const {id, sequentialId, team, agentIdentity, tags} = ticket;

    let tag = (tags != undefined && tags != '') ? tags.toString() : '';

    const npsBotOrHuman = botOrHuman({id});
    const ticketInfoOpenDate = ticketOpenDateFormated(ticket);

    const csatExibicao = `${cpf || ''} | ${npsBotOrHuman} | ${id || ''} | ${sequentialId || ''} | ${ticketInfoOpenDate} | ${botId} | ${botName} | ${contactIdentity || ''} | ${userName || ''} | ${phoneNumber || ''} | ${team || ''} | ${agentIdentity || ''} | ${tag || ''}`;
    const csatExibInesperado = `${npsBotOrHuman} | ${id || ''} | ${sequentialId || ''}`;

    return {
        csatExibicao,
        csatExibInesperado,
        npsBotOrHuman
    };
}

function ticketOpenDateFormated(ticket) {
    _ticket = ticket;

    if(_ticket.openDate){
        let date = new Date(_ticket.openDate);
        let day = date.getUTCDate() < 10 ? "0" + date.getUTCDate() : date.getUTCDate();
        let month = (date.getUTCMonth()+1) < 10 ? "0" + (date.getUTCMonth()+1) : (date.getUTCMonth()+1);
        let hour = (date.getUTCHours()-3) < 10 ? "0" + (date.getUTCHours()-3) : (date.getUTCHours()-3);
        let minutes = date.getUTCMinutes() < 10 ? "0" + date.getUTCMinutes() : date.getUTCMinutes();
        let seconds =  date.getUTCSeconds() < 10 ? "0" +  date.getUTCSeconds() :  date.getUTCSeconds();
        let newDate = `${day}/${month}/${date.getUTCFullYear()} ${hour}:${minutes}:${seconds}`;
        return newDate;
    }

    return "";
}

function botOrHuman({id = "bot"}) {
    return id == "bot" ? "bot" : "human";
}

const validateRegexOptionsAccordingToInputContent = (inputContent, regexOptions, invalid = "Input inesperado") => {
    regexOptions = { ...regexOptions, "^(Sair)$": "Sair", "^(Voltar)$": "Voltar", "^(Menu|Menu\\sInicial)$": "Menu" }
    for (let key in regexOptions) {
        let matching = new RegExp(key, "i");

        if (matching.test(inputContent)) {
            return regexOptions[key];
        }
    }

    return invalid;
}

function run(inputContent) {
    try {
        const REGEX_OPTIONS = {
            "(^1$|^(S|s)im$|^1. |(S|s)im$)": "Sim",
            "(^2$|^(N|n)(ã|a)o$|^2. |(N|n)(ã|a)o$)": "Não",
