function validaData(data) {
  if (!/^(\d{2})[-/](\d{2})[-/](\d{4})$/.test(data)) {
    return false;
  }

  const [dia, mes, ano] = data.split(/[-/]/).map(Number);

  if (ano < 1000 || ano > 3000 || mes < 1 || mes > 12)
    return false;

  let diaMaximo;
  switch (mes) {
    case 4:
    case 6:
    case 9:
    case 11:
      diaMaximo = 30;
      break;
    case 2:
      if ((ano % 4 === 0 && ano % 100 !== 0) || ano % 400 === 0)
        diaMaximo = 29;
      else
        diaMaximo = 28;
      break;
    default:
      diaMaximo = 31;
  }

  return dia > 0 && dia <= diaMaximo;
}

