	}
}

function run(voltarState, botOrigin) {
	try {
		const { botOrigin } = JSON.parse(botOrigin)
		const state = JSON.parse(voltarState)
		if (state[botOrigin].length === 0) {
			return state
		}
		state[botOrigin].pop()
		return state
	} catch (err) {
		return false
	}
}

function run(voltar, state, stateOrigin, lastInput, intent, env) {
	try {
		const parsedState = JSON.parse(state)
		const voltarState = JSON.parse(voltar)
		if (voltar == false) {
			throw new Error('reset')
		}

		if (parsedState.botOrigin !== `Bot Main ${env || ''}`.trim() && voltarState[parsedState.botOrigin].length === 0) {
			return {
				botDestination: `Bot Main ${env || ''}`.trim(),
				botOrigin: 'cascata',
				stateDestination: voltarState[`Bot Main ${env || ''}`.trim()][voltarState[`Bot Main ${env || ''}`.trim()].length - 1],
				stateOrigin,
				lastInput,
				intent
			}
		}
		const id = voltarState[parsedState.botOrigin][voltarState[parsedState.botOrigin].length - 1]
		return {
			botDestination: parsedState.botOrigin,
			botOrigin: 'cascata',
			stateDestination: id,
			stateOrigin,
			lastInput,
			intent
		}
	} catch (err) {
		return {
			botOrigin: 'cascata',
			stateOrigin,
			botDestination: `Bot Main ${env || ''}`.trim(),
			stateDestination: 'welcome',
			err: err.message
		}
	}
}

function checkVariables(contaSelecionada, selectedEc) {
  try {
    let variables = []
    if (contaSelecionada) {
      contaSelecionada = JSON.parse(contaSelecionada)
      const keyContaSelecionada = Object.keys(contaSelecionada)
      keyContaSelecionada.forEach((key) => {
        if (contaSelecionada[key]) {
          return variables.push(key)
        }
      })
    }
    if (selectedEc) {
      selectedEc = JSON.parse(selectedEc)
      const keySelectedEc = Object.keys(selectedEc)
      keySelectedEc.forEach((key) => {
        if (selectedEc[key]) {
          return variables.push(key)
        }
      })
    }
    return variables
  } catch (err) {
    return []
  }
}

function run(intencaoIdentificada, contaSelecionada, selectedEc, intentDestination) {
  try {
    const intent = JSON.parse(intentDestination).find(intent => intent.intent.toLowerCase() === intencaoIdentificada.toLowerCase())
    const userContext = {
      variables: checkVariables(contaSelecionada, selectedEc),
      destinationBlock: '',
      allowed: false,
      auth: false
    }
    if (intent) {
      if (intent.requirements.length === 0 || intent.requirements[0] === '') {
        userContext.allowed = true;
        userContext.destinationBlock = intent.destinationBlock;
        userContext.destinationBot = intent.destinationBot
      } else {
        userContext.allowed = intent.requirements.every(requirement => userContext.variables.includes(requirement));
        if (userContext.allowed) {
          userContext.destinationBlock = intent.destinationBlock;
          userContext.destinationBot = intent.destinationBot
          userContext.auth = intent.auth
        }
      }
    }
    return userContext
  } catch (err) {
    return {
      allowed: false,
      destinationBlock: '',
      error: err.message
    }
  }
}

function run(stateDestination, previousStateName, lastInput, intent, serviceName, env, previousBlockId) {
  try {
    let match = previousStateName.match(/\[(.*?)\]/);
    let stateNameOrigin = match ? match[1] : previousStateName;
    const parsedUserContext = JSON.parse(stateDestination)
    let botDestination = parsedUserContext.destinationBot

    return {
      botOrigin: serviceName,
      stateOrigin: previousStateName,
      stateNameOrigin,
      botDestination: `${parsedUserContext.destinationBot} ${env || ''}`.trim(),
      stateDestination: `${parsedUserContext.destinationBlock}`,
      lastInput: lastInput || intent,
