		return {
			botOrigin: serviceName,
			stateOrigin: 'e2658701-703d-4150-944c-3daba99cfbd3',
			stateNameOrigin: previousStateName.replace(/.*\[(.*?)\].*/, '$1'),
			botDestination: `Bot Máquina Safrapay ${env || ""}`.trim(),
			stateDestination: 'f0a8601f-5de1-4885-9b44-4f4a6e062c01',//MS.7.0.1
			lastInput: lastInput || intent,
			intent,
			userInput: intent
		}
