/**
* All input variables needs to be passed as function param;
* Objects received as param needs to be parsed. Ex.: JSON.parse(inputVariable1);
* Objects returned needs to be stringfied. Ex.: JSON.stringify(inputVariable1);
**/
function run(counter, state) {
	const parsedState = JSON.parse(state)
	try {
		if (!counter) {
			return {
				counter: 1,
				lastErrorState: parsedState.stateOrigin
			}
		}
		const parsedCounter = JSON.parse(counter)
		if (parsedCounter.lastErrorState == parsedState.stateOrigin) {
			parsedCounter.counter++
			return parsedCounter
		}
		return {
			counter: 1,
			lastErrorState: parsedState.stateOrigin
		}
	} catch (err) {
		return {
			counter: 1,
			lastErrorState: parsedState.stateOrigin,
			err: err.message
		}
	}
}

function run(state, serviceName) {
	try {
		const { botDestination, stateDestination } = JSON.parse(state)

		if (stateDestination.match(/^[0-9a-fA-F]{8}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{4}\b-[0-9a-fA-F]{12}$/) && botDestination === serviceName) {
			return stateDestination
		}
		return false
	} catch (err) {
		return false
	}
}

