		let textoContas = ''
		contas.slice(0, 100)
			.forEach((conta, index) => textoContas += `*${index + 1}* - AG ${replaceAt(conta.agencia, 1, "XXX")}\nCC ${replaceAt(conta.conta, 2, "XXXX")}\n`)
		return textoContas
