			errorMessage: err.message,
			tracking: `${idSessao} | ${stateName} | ${err.message}`
		}
	}
}

function run(contaSelecionada, bodyDadosConta, bodyUsuarios, cpf) {
	try {

		const { tipoPessoa, baseDocumento, filialCnpj, digitoCpf } = JSON.parse(bodyDadosConta).data
		const conta = JSON.parse(contaSelecionada)
		const parsedBodyUsuarios = JSON.parse(bodyUsuarios).data

		conta['tipoPessoa'] = tipoPessoa
		conta['doc'] = cpf
		if (conta.usuario) {
			conta['usuario'] = conta.usuario.trim()
		}
		if (tipoPessoa === 'PJ') {
			let doc = baseDocumento + "000" + filialCnpj
			let digito = digitoCpf
			if (digitoCpf < 10) {
				digito = "0"+digitoCpf
			}
			conta['doc'] = baseDocumento + "000" + filialCnpj + digito
		}
		if (parsedBodyUsuarios.length > 0) {
			let primeiroNome = parsedBodyUsuarios.find((usuario) => conta.usuario.trim() === usuario.id.trim()).nome.split(' ')[0].toLowerCase()
			primeiroNome = primeiroNome[0].toUpperCase() + primeiroNome.slice(1)
			conta['nomeCompleto'] = parsedBodyUsuarios.find((usuario) => conta.usuario.trim() === usuario.id.trim()).nome
			conta['primeiroNome'] = primeiroNome
		} else {
			conta['nomeCompleto'] = ''
			conta['primeiroNome'] = ''
		}
		return conta
	} catch (err) {
		return err.message
