			stateDestination: '621b7935-89f3-450d-87b1-6caf539d5ec7'
		},
		'cartao': {
			botDestination: `Bot Main${env || ""}`.trim(),
			stateDestination: '621b7935-89f3-450d-87b1-6caf539d5ec7'
		},
		'pacotes contratados': {
			botDestination: `contacorrente${env || ""}`.trim(),
			stateDestination: 'daa2d803-2548-400e-8782-47ecaec9aca1'
		}
	}
	try {
		let match = previousStateName.match(/\[(.*?)\]/);
		let stateNameOrigin = match ? match[1] : previousStateName;
		const destinationBot = destination[assunto]
		if (!destinationBot) {
			throw new Error('invalid destination')
		}
