function run(extratoResponse, dataExtractor) {
  try {
    const [inicial, final] = dataExtractor.split(' - ')
    const [anoInicial, mesInicial, diaInicial] = inicial.split('-')
    const [anoFinal, mesFinal, diaFinal] = final.split('-')
    const periodo = `extrato_de_${diaFinal}-${mesFinal}-${anoFinal}_a_${diaInicial}-${mesInicial}-${anoInicial}.pdf`
    const { data } = JSON.parse(extratoResponse)
    if (!data.urlDocumento) {
      return false
    }
    dynamicContent.document.link = data.urlDocumento
    // dynamicContent.document.caption = 'teste'
    dynamicContent.document.filename = periodo
    return dynamicContent
  } catch (err) {
    return false
  }
}

function run(userInput) {
	try {
		const userInputLimpo = parseInt(userInput.replace(/[^0-9]/g, ''))
		if (userInputLimpo < 0) {
			return 'input inesperado'
		}
		if (userInputLimpo > 90) {
			return 'input inesperado'
		}
		if (isNaN(userInputLimpo)) {
			return 'input inesperado'
		}

		const today = new Date()
		const days = userInputLimpo
		const pastDate = new Date(today)
		pastDate.setDate(today.getDate() - days)
		if (!pastDate) return 'input inesperado'
		return `${today.toISOString().split('T')[0]} - ${pastDate.toISOString().split('T')[0]}`
	} catch (err) {
		return 'input inesperado'
	}
}

