function ignoreBlock(stateName) {
    try {
        const match = stateName.match(/\[(.*?)\]/);
        const stateNameOrigin = match ? match[1] : stateName;
        const regex = /Redirect|Redirecionar/i;
        const ignore = regex.test(stateNameOrigin);
        return ignore;
    } catch (err) {
        return false
    }
}


function run(voltarState, blockId, botOrigin, lastVoltarState, previousStateName) {
    const state = JSON.parse(voltarState)
    try {
        if (!voltarState) {
            const state = {}
            state[botOrigin] = [blockId]
            return state
        }
        const isIgnoredBlock = ignoreBlock(previousStateName)
        if (state[botOrigin][state[botOrigin].length - 1] !== blockId && blockId !== 'onboarding' && !isIgnoredBlock && blockId !== lastVoltarState) {
            state[botOrigin].push(blockId)
        }
        return state
    } catch (err) {
        state[botOrigin] = [blockId]
        return state
    }
}
