function run(errType, errorCounter) {
	try {
		if (!errType) {
			return false
		}
		let { counter } = JSON.parse(errorCounter)
		if (parseInt(counter) == 1) {
			return MEDIA_TYPES[errType] || false
		}
		if (parseInt(counter) == 2) {
