	}
}

function run(previousBlockid, previousStateName, lastInput, intent, serviceName, env,state) {
	try {
		let parsedState = JSON.parse(state)
		return {
			botOrigin: serviceName,
			stateOrigin: previousBlockid,
			stateNameOrigin: previousStateName.replace(/.*\[(.*?)\].*/, '$1'),
			botDestination: parsedState.botOrigin,
			stateDestination: parsedState.stateOrigin,
			lastInput: lastInput || intent,
			intent
		}
	} catch (error) {
		return {
			botOrigin: serviceName,
			stateOrigin: previousBlockid,
			stateNameOrigin: previousStateName.replace(/.*\[(.*?)\].*/, '$1'),
			botDestination: `Bot Main ${env || ""}`.trim(),
			stateDestination: 'welcome',
			error: error.message,
			lastInput
		}
	}
}

