			}
		}
	} catch (err) {
		return {
			"id": "{{random.guid}}",
			"to": "postmaster@ai.msging.net",
			"method": "set",
			"uri": "/analysis",
			"type": "application/vnd.iris.ai.analysis-request+json",
			"resource": {
				"text": inputContent
			}
		}
	}
}

function run(intent, accuracy) {
	try {
		input = JSON.parse(intent).resource.text;
		return {
			input: input,
			intent: findIntention(intent, accuracy)
		}
	}
	catch (e) {
		return e.message;
	}
}

function findIntention(intent, accuracy) {
	try {
		intent = JSON.parse(intent).resource.intentions
		indentifiedIntent = intent.filter(({ score }) => score > Number(accuracy))
		if (indentifiedIntent.length > 0) {
			return indentifiedIntent[0].name;
		}
		return null
	} catch (err) {
		return null
	}
}

function run(bodySmallTalks) {
	try {
		let data = JSON.parse(bodySmallTalks);
		if (data && data.analysis && data.analysis.matches) {
			return data.analysis.matches.map(match => match.smallTalk);
		}
		return [];
	}
	catch (err) {
		return [];
	}
}

const run = (contact, intent) => {
	try {
		const intencao = JSON.parse(intent)
		const dataRaw = new Date()
		dataRaw.setHours(new Date().getHours() - 3)
		const data = dataRaw.toISOString().split("T")[0]
		const hora = dataRaw.toISOString().split("T")[1].split('.')[0]

		const intencaoIdentificada = intencao.intent ? intencao.intent : "nao identificada"

		const telefone = contact.split("@")[0]
		return `${telefone} | ${data} | ${hora} | ${intencao.input} | ${intencaoIdentificada}`
	}
	catch (e) {
		return e.message;
	}

}

