const run = (contact, intent, originator) => {
	try {
		const intencao = JSON.parse(intent)
		const dataRaw = new Date()
		dataRaw.setHours(new Date().getHours() - 3)
		const data = dataRaw.toISOString().split("T")[0]
		const hora = dataRaw.toISOString().split("T")[1].split('.')[0]

		const intencaoIdentificada = intencao.intent ? intencao.intent : "nao identificada"
		let routerParts = originator.split("@");
		let router = routerParts[0];
		let linkCv = `https://safra2.blip.ai/application/detail/${router}/users/${contact}`

		const telefone = contact.split("@")[0]
		return `${telefone} | ${data} | ${hora} | ${intencao.input} | ${intencaoIdentificada} | ${linkCv}`
	}
	catch (e) {
		return e.message;
	}
}

