function run(contactIdentity) {
  const DEFAULT_CHANNEL = "other";
  const GOOGLE_CHANNEL = "gbm";
  const WHATSAPP_CHANNEL = "whatsapp";
  const INSTAGRAM_CHANNEL = "instagram";
  const BLIPCHAT_CHANNEL = "blipchat";
  const FACEBOOK_CHANNEL = "facebook";

  const WHATSAPP_IDENTIFIER = "wa.gw.msging.net";
  const BLIPCHAT_IDENTIFIER = "0mn.io";
  const GOOGLE_IDENTIFIER = "businessmessages.gw.msging.net";
  const FACEBOOK_IDENTIFIER = "messenger.gw.msging.net";
  const INSTAGRAM_IDENTIFIER = "instagram.gw.msging.net";

  const CHANNEL_INDEX = 1;
  const contactChannelIdentifier = contactIdentity.split("@")[CHANNEL_INDEX];

  switch (contactChannelIdentifier) {
    case WHATSAPP_IDENTIFIER:
      return WHATSAPP_CHANNEL;

    case BLIPCHAT_IDENTIFIER:
      return BLIPCHAT_CHANNEL;

    case FACEBOOK_IDENTIFIER:
      return FACEBOOK_CHANNEL;

    case INSTAGRAM_IDENTIFIER:
      return INSTAGRAM_CHANNEL;

    case GOOGLE_IDENTIFIER:
      return GOOGLE_CHANNEL;

    default:
      return DEFAULT_CHANNEL;
  }
}

function run(userChannel) {
  const WHATSAPP_CHANNEL = "whatsapp";
  const BLIPCHAT_CHANNEL = "blipchat";
  const FACEBOOK_CHANNEL = "facebook";
  const INSTAGRAM_CHANNEL = "instagram";
  const GOOGLE_CHANNEL = "gbm";

  switch (userChannel) {
    case WHATSAPP_CHANNEL:
      return {
        open: "*",
        close: "*",
      };
    case BLIPCHAT_CHANNEL:
    case GOOGLE_CHANNEL:
      return {
        open: "<b>",
        close: "</b>",
      };
    case FACEBOOK_CHANNEL:
    case INSTAGRAM_CHANNEL:
      return {
        open: "",
        close: "",
      };
  }

  return {
    open: "<b>",
    close: "</b>",
  };
}

