function run(bold, platform, customerAccounts) {
    platform = platform.toLowerCase();
    if (platform === "instagram" || platform === "telegram" || platform === "messenger")
      platform = 'blipchat';

    customerAccounts = JSON.parse(customerAccounts);

    const accounts = customerAccounts.accounts;
    const text = `Escolha qual a conta você quer
seguir com o atendimento.`;
    let isButton = false;
    let isList = true;
    const description = [];
    const options = [];
    accounts.forEach((account, index) => {
        options.push(`Conta: ${account.numeroConta}`);
    });
   accounts.forEach((account, index) => {
        description.push(`AG: ${account.numeroAgencia}`);
    });

    options.push("Encerrar atendimento");

    if(options.length>10){
        platform = "blipchat";
    }

    const menuSettings = {
        text,
        "header": '',
        "body": text,
        "footer": '',
        "button": 'Escolher conta',
        "title": '',
        "namespaceTemplate": '',
        "nameTemplate": '',
        options,
        "values": options,
        "description": description,
        "menuScope": {
            "whatsappButton": isButton,
            "whatsappList": isList,
            "whatsappTemplate": false,
            "blipchatQuickReply": isButton,
            "blipchatMenu": isList,
            "defaultText": false,
            platform,
            "boldValue": bold,
        },
    };

    return getMenuForPlatform(menuSettings);
}

function capitalizeText(text) {
  let words = text.split(" ");
  words = words.map(
    (word) =>
      word.length &&
      `${word.charAt(0).toUpperCase()}${word.slice(1).toLowerCase()}`
  );
  return words.join(" ");
}

function run(input, inputType, customerAccounts) {
  const error = JSON.stringify({
    status: "input inválido",
    input: input ? input : "usuário inativo",
    isValid: false,
    type: "menuDinamico",
  });

  if (inputType !== "text/plain") return error;

  customerAccounts = JSON.parse(customerAccounts);
  input = input.trim();

  // A opção e o número da opção são incluidos automaticamente na regexp
  const menuOptions = {
    "Encerrar atendimento": ["encerrar", "sair"],
  };
  const options = ["Encerrar atendimento"];
  const accounts = customerAccounts.accounts;
  const charLimit = 24;

  accounts.forEach((account) => {
    const textOption = `Conta: ${account.numeroConta}`;

    if (textOption.length < charLimit) {
      menuOptions[account.numeroConta] = [textOption];
    } else {
      menuOptions[account.numeroConta] = [];
    }

    options.push(account.numeroConta);
  });

  const matches = options.find((option, index) => {
    let regexp = `^${option}$|`;
    menuOptions[option].forEach((validationString) => {
      validationString = validationString.replace(/^\^|\$$/g, "");
      regexp += `^${validationString}$|`;
    });
    regexp += `^${index}$`;

    return input.match(new RegExp(regexp, "gim"));
  });

  return matches
    ? JSON.stringify({
        status: "input válido",
        input: matches,
        isValid: true,
        type: "menuDinamico",
      })
    : error;
}

